package de.bennyboer.kicherkrabbe.app;

import de.bennyboer.kicherkrabbe.list.LinkedList;

import static de.bennyboer.kicherkrabbe.utilities.StringUtils.join;
import static de.bennyboer.kicherkrabbe.utilities.StringUtils.split;
import static de.bennyboer.kicherkrabbe.app.MessageUtils.getMessage;

import org.apache.commons.text.WordUtils;

public class App {
    public static void main(String[] args) {
        LinkedList tokens;
        tokens = split(getMessage());
        String result = join(tokens);
        System.out.println(WordUtils.capitalize(result));
    }
}
